import{_ as a,c as n,o as t,ag as o}from"./chunks/framework.Bw-5EFTY.js";const u=JSON.parse('{"title":"üèóÔ∏è Architecture Overview","description":"","frontmatter":{},"headers":[],"relativePath":"other/architecture-overview.md","filePath":"en/other/architecture-overview.md","lastUpdated":1752654124000}'),i={name:"other/architecture-overview.md"};function r(s,e,l,c,d,p){return t(),n("div",null,e[0]||(e[0]=[o(`<h1 id="üèóÔ∏è-architecture-overview" tabindex="-1">üèóÔ∏è Architecture Overview <a class="header-anchor" href="#üèóÔ∏è-architecture-overview" aria-label="Permalink to &quot;üèóÔ∏è Architecture Overview&quot;">‚Äã</a></h1><p>Notivae is designed as a modular, scalable, and self-hosted collaborative markdown editor. Understanding its architecture helps you grasp how components interact and how to deploy, extend, or contribute to the project.</p><h2 id="high-level-components" tabindex="-1">High-Level Components <a class="header-anchor" href="#high-level-components" aria-label="Permalink to &quot;High-Level Components&quot;">‚Äã</a></h2><ul><li><p><strong>Frontend (Vue + Vite)</strong><br> A modern single-page application providing the user interface for editing, navigation, commenting, and collaboration.</p></li><li><p><strong>Backend API (Python FastAPI)</strong><br> Serves REST and WebSocket endpoints to handle authentication, document management, versioning, access control, and CRDT-based synchronization.</p></li><li><p><strong>Database (PostgreSQL)</strong><br> Stores users, workspaces, collections, documents, snapshots, comments, permissions, etc.</p></li></ul><h2 id="component-interaction" tabindex="-1">Component Interaction <a class="header-anchor" href="#component-interaction" aria-label="Permalink to &quot;Component Interaction&quot;">‚Äã</a></h2><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>+----------+            +--------------+          +----------------+</span></span>
<span class="line"><span>| Frontend | &lt;--REST--&gt; | Backend      | &lt;--DB--&gt; | Database       |</span></span>
<span class="line"><span>| (Vue)    | &lt;---WS---&gt; | (FastAPI)    |          | (PostgreSQL)   |</span></span>
<span class="line"><span>+----------+            +--------------+          +----------------+</span></span></code></pre></div><h2 id="deployment-model" tabindex="-1">Deployment Model <a class="header-anchor" href="#deployment-model" aria-label="Permalink to &quot;Deployment Model&quot;">‚Äã</a></h2><ul><li>All components run inside Docker containers orchestrated via <code>docker-compose</code>.</li><li>Ports are exposed for the frontend and backend.</li><li>Persistent storage is configured for database files and user data.</li></ul><h2 id="offline-first-and-sync" tabindex="-1">Offline-First and Sync <a class="header-anchor" href="#offline-first-and-sync" aria-label="Permalink to &quot;Offline-First and Sync&quot;">‚Äã</a></h2><ul><li>The frontend keeps a local replica of documents.</li><li>Changes are applied locally and synced in the background via CRDTs.</li><li>This approach guarantees no lost edits, even when offline or on unstable connections.</li></ul><h2 id="security-and-access-control" tabindex="-1">Security and Access Control <a class="header-anchor" href="#security-and-access-control" aria-label="Permalink to &quot;Security and Access Control&quot;">‚Äã</a></h2><ul><li>Authentication is handled by the backend with support for email/password and OAuth/OIDC.</li><li>Access to documents and collections is controlled using ABAC policies evaluated on the backend.</li><li>All communications are secured via HTTPS in production (reverse proxy recommended).</li></ul><hr><p>This modular design enables Notivae to be extended with features like API integrations, and enhanced collaboration tools without breaking core principles of privacy, ownership, and offline resilience.</p>`,14)]))}const m=a(i,[["render",r]]);export{u as __pageData,m as default};
