import{_ as i,c as a,o,ag as t}from"./chunks/framework.Bw-5EFTY.js";const h=JSON.parse('{"title":"🛣️ Roadmap","description":"","frontmatter":{},"headers":[],"relativePath":"other/roadmap.md","filePath":"en/other/roadmap.md","lastUpdated":1752048603000}'),n={name:"other/roadmap.md"};function l(r,e,s,d,c,u){return o(),a("div",null,e[0]||(e[0]=[t('<h1 id="🛣️-roadmap" tabindex="-1">🛣️ Roadmap <a class="header-anchor" href="#🛣️-roadmap" aria-label="Permalink to &quot;🛣️ Roadmap&quot;">​</a></h1><p>Notivae is in <strong>early-stage development</strong>. No features are fully implemented yet — everything listed below is planned but still in progress or under design. This roadmap gives a transparent view of where the project is heading.</p><h2 id="🧱-planned-foundation" tabindex="-1">🧱 Planned Foundation <a class="header-anchor" href="#🧱-planned-foundation" aria-label="Permalink to &quot;🧱 Planned Foundation&quot;">​</a></h2><ul><li>Personal workspace per user</li><li>Markdown-based document editor</li><li>Collections for organizing documents</li><li>Document nesting and hierarchy</li><li>Offline-first editing</li><li>Sync-on-connect model using CRDTs</li><li>Snapshot-based versioning</li><li>Inline comments and discussions</li><li>Email/password authentication</li><li>OIDC-based third-party login</li><li>Self-hostable via Docker Compose</li></ul><h2 id="🛠️-upcoming-development-focus" tabindex="-1">🛠️ Upcoming Development Focus <a class="header-anchor" href="#🛠️-upcoming-development-focus" aria-label="Permalink to &quot;🛠️ Upcoming Development Focus&quot;">​</a></h2><ul><li>Frontend: editor UI, navigation, commenting</li><li>Backend: data model, auth, syncing, access control</li><li>Database schema</li><li>First working end-to-end Docker setup</li><li>Persistent volume storage and backup support</li><li>CI for ghcr.io image builds</li></ul><h2 id="🧪-future-features" tabindex="-1">🧪 Future Features <a class="header-anchor" href="#🧪-future-features" aria-label="Permalink to &quot;🧪 Future Features&quot;">​</a></h2><ul><li>Public sharing via links</li><li>Invite-based sharing with per-user access control</li><li>Full-text search across documents</li><li>Export to PDF / Markdown</li><li>Admin interface</li><li>Comment threading &amp; resolution</li><li>Snapshot diffs</li><li>Configurable themes</li><li>REST and WebSocket APIs</li><li>Mobile-first PWA enhancements</li></ul><h2 id="🧭-long-term-vision" tabindex="-1">🧭 Long-Term Vision <a class="header-anchor" href="#🧭-long-term-vision" aria-label="Permalink to &quot;🧭 Long-Term Vision&quot;">​</a></h2><ul><li>Shared workspaces (teams, organizations)</li><li>Plugin system for extensibility</li><li>Self-hosted AI integration (summarization, search)</li><li>Custom metadata fields for documents</li><li>Workspace analytics and activity feed</li></ul><hr><blockquote><p>🚧 Everything is subject to change.<br> You can track progress or contribute on <a href="https://github.com/notivae/notivae" target="_blank" rel="noreferrer">GitHub</a>.</p></blockquote>',12)]))}const p=i(n,[["render",l]]);export{h as __pageData,p as default};
